import { EventHandler } from '@krogan/common';
import { Button } from 'gui/uielements';
import React, { CSSProperties, Fragment } from 'react';
import { Container } from '.';

/**
 * Render the controls
 */

export interface ControlsProps {
	style?: CSSProperties
	backButton: boolean
	mapButton: boolean
	eventHandler: EventHandler
}

export interface ControlsState {}

class ControlsView extends React.Component<ControlsProps, ControlsState> {


	render() {
		return (
		<Container
			style={{
				...styles.controls,
				...this.props.style,
			}}
		>
			<Fragment>
				{this.props.mapButton ?
				<>
					<Button 
						position='left'
						style={styles.button}
						onClick={() => this.props.eventHandler.send('setZoom', 0)}
					>MAP</Button>
					<Button 
						position='center'
						style={styles.button}
						onClick={() => this.props.eventHandler.send('setZoom', 1)}
					>HEX</Button>
					<Button 
						position='right'
						style={styles.button}
						onClick={() => this.props.eventHandler.send('setZoom', 2)}
					>SHIP</Button>
				</>
				: this.props.backButton && // TODO: remove this button and leave only these 3
					<Button 
						style={styles.button}
						onClick={() => this.props.eventHandler.send('back', {})}
					>BACK</Button>
				}
			</Fragment>
		</Container>
		)
	}

}

export const Controls = React.memo(ControlsView)


const styles: { [key: string]: CSSProperties } = {
    controls: {
        flexDirection: 'row',
        alignItems: 'flex-start',
        minHeight: 40,
	},

	button: {
		marginBottom: 8
	}
};
