import { Asteroid } from 'state/Asteroid';
import { getCacheAsset } from 'images/textures';
import { Sprite } from 'pixi.js';
import { ClickableContainer } from 'sprites/containers';
import { EventHandler, Maths } from '@krogan/common';

/**
 * A sprite representing an asteroid with circle bounds.
 */
export default class AsteroidSprite extends ClickableContainer {

	private sprite: Sprite

    constructor(
        asteroid: Asteroid,
		eventHandler: EventHandler
    ) {
		const textures = [
			'asteroid-empty-1', 
			'asteroid-empty-2', 
			'asteroid-empty-3', 
			'asteroid-empty-4', 
			'asteroid-empty-5'
		]
		const key = `${textures[asteroid.size]}-${Maths.getRandomInt(1,2)}`
		const texture = getCacheAsset(key)
        super(asteroid.radius, undefined, () => {
			eventHandler.send('asteroidClick', asteroid)
		})
		// TODO: show a helper onHover

		this.position.set(asteroid.position.x, asteroid.position.y)

		this.sprite = new Sprite(texture)
		this.addChild(this.sprite)

		this.sprite.anchor.set(0.5)
        this.width = asteroid.radius * 2
        this.height = asteroid.radius * 2
		this.angle = asteroid.position.angle
    }
}
