import { Container, DisplayObject } from 'pixi.js';

export default class ManagerContainer<T> extends Container {
    protected entities: { [key: string]: T & DisplayObject};

    constructor(name: string) {
        super();
        this.name = name
        this.entities = {}
    }

    // Container
    public hide = () => {
        this.visible = false
    };

    public show = () => {
        this.visible = true
    };

    // Entities
    public add = (key: string, entity: T & DisplayObject) => {
        this.addChild(entity)
        this.entities[key] = entity
    };

    public get = (key: string): (T & DisplayObject) | undefined => {
        return this.entities[key]
    };

    public getAll = (): T[] => {
        return Object.values(this.entities)
    };

    public getAllKeys = (): string[] => {
        return Object.keys(this.entities)
    };

    public length = (): number => {
        return Object.keys(this.entities).length
    }

    public remove = (key: string) => {
        this.removeChild(this.entities[key])
        delete this.entities[key]
    };
}
