import * as PIXI from 'pixi.js';
import { ManagerContainer } from 'sprites/containers';
import { Constants, Entities, Geometry, Maths, Models, EventHandler } from '@krogan/common';
import { AsteroidSprite, SolarSystem, SpaceshipSprite } from '../sprites';
import { Viewport } from 'pixi-viewport';
import { Player } from '../state/Player';
import { GameState } from 'state/GameState';
import { Graphics, IPoint } from 'pixi.js';
import { MapZINDEX } from './MapManager';
import { Spaceship } from 'state/Spaceship';
import { SolarSystemSize } from 'sprites/map/SolarSystem';
import { Asteroid } from 'state/Asteroid';
import { Bullet } from 'state/Bullet';
import BulletSprite from 'sprites/game/BulletSprite';
import ParallaxController from 'sprites/controllers/ParallaxController';
import { playerManager } from './PlayerManager';
import { Starbase } from 'state/Starbase';
import StarbaseSprite from 'sprites/game/StarbaseSprite';

enum ZINDEXES {
    BACKGROUND,
    PLANETS,
    STARBASES,
    ASTEROIDS,
    BULLETS,
    PLAYERS,
    ME,
}

// TODO: These two constants should be calculated automatically.
// They are used to interpolate movements of other players for smoothness.
const TOREMOVE_MAX_FPS_MS = 1000 / 60;
const TOREMOVE_AVG_LAG = 50;

interface IInputs {
    left: boolean;
    up: boolean;
    right: boolean;
    down: boolean;
}

export class GameManager {
    // Inputs
    public inputs: IInputs = {
        left: false,
        up: false,
        right: false,
        down: false,
    }
    public pointer = new PIXI.Point(0, 0)

    public forcedRotation = 0 // Used on mobile only

    // Callbacks
    public sendToServer?: (action: Models.ActionJSON) => void
    public changeHudSpaceship?: (spaceship: SpaceshipSprite) => void

    // Application
    private app: PIXI.Application

    // ViewPorts
    private worldView: Viewport

    // Containers
    private spaceshipContainer: ManagerContainer<SpaceshipSprite>
    private asteroidContainer: ManagerContainer<AsteroidSprite>
    private starbasesContainer: ManagerContainer<StarbaseSprite>
    private bulletContainer: ManagerContainer<BulletSprite>
    private solarSystem: SolarSystem
    private parallax: ParallaxController

    // Game
    private roomName?: string // todo: dispatch it to hud insted of setting here
    private state?: GameState

    // Me (the one playing the game on his computer)
    private currentSpaceship?: SpaceshipSprite | null = null

    // Server reconciliation
    private moveActions: Models.ActionJSON[] = []

    // Game Events
    public eventHandler: EventHandler

    // Map data
    private map: Entities.Map

    // LIFECYCLE
    constructor(app: PIXI.Application, map: Entities.Map, eventHandler: EventHandler) {
        this.app = app
        this.map = map
        this.eventHandler = eventHandler

        // this.app.ticker.add(this.update, undefined, PIXI.UPDATE_PRIORITY.HIGH)
        // this.app.ticker.maxFPS = Constants.FPS

        // World View
        this.worldView = new Viewport({
            screenWidth: window.innerWidth,
            screenHeight: window.innerHeight,
            worldWidth: Constants.ZOOM_MAP_RADIUS * 2,
            worldHeight: Constants.ZOOM_MAP_RADIUS * 2,
            interaction: this.app.renderer.plugins.interaction
        })
        this.app.stage.addChild(this.worldView)
        this.worldView.zIndex = MapZINDEX.GAME

        this.worldView
