    // PUBLIC METHODS

    assetsLoaded = () => {
        // empty
    }

    hideAll = () => {
        this.worldView.visible = false
    }

    showAll = () => {
        this.worldView.visible = true
    }

    // PRIVATE METHODS

    private update = () => {
        this.updateInputs()
        this.updateSpaceships()
        this.rotateShield()
    };

    // Lerp server players
    private updateSpaceships = () => {

        this.selfControlMoveIfNeeded()

        for (const spaceship of this.spaceshipContainer.getAll()) {
            if (Math.abs(spaceship.toX - spaceship.x) > 0.0001 || Math.abs(spaceship.toY - spaceship.y) > 0.0001) {

                spaceship.position.x = Maths.lerp(spaceship.x, spaceship.toX, TOREMOVE_MAX_FPS_MS / TOREMOVE_AVG_LAG)
                spaceship.position.y = Maths.lerp(spaceship.y, spaceship.toY, TOREMOVE_MAX_FPS_MS / TOREMOVE_AVG_LAG)

                spaceship.moving = true
            } else {
                spaceship.moving = false
            }
        }
    };

    // LOCAL SIMULATION

    private updateInputs = () => {
        // Move
        const dir = new Geometry.Vector2(0, 0);
        if (this.inputs.up || this.inputs.down || this.inputs.left || this.inputs.right) {
            if (this.inputs.up) {
                dir.y -= 1;
            }

            if (this.inputs.down) {
                dir.y += 1;
            }

            if (this.inputs.left) {
                dir.x -= 1;
            }

            if (this.inputs.right) {
                dir.x += 1;
            }
        }

        if (dir.isEmpty) {
            if (this.currentSpaceship?.shouldSlide()) {
                this.move(dir)
                if (this.currentSpaceship) this.currentSpaceship.moving = true
            } else {
                if (this.currentSpaceship) this.currentSpaceship.moving = false
            }
        } else {
            this.move(dir);
            if (this.currentSpaceship) this.currentSpaceship.moving = true
        }
    };

    // ACTIONS

    public createAndSendAction = (type: Models.ActionType, data: any): Models.ActionJSON | undefined => {
        if (!this.currentSpaceship) return undefined

        const action: Models.ActionJSON = {
            type: type,
            ts: Date.now(),
            nonce: this.currentSpaceship.nonce,
            value: data
        };
        this.sendToServer?.(action)

        return action
    }

    public moveToDestination = (point: IPoint) => {
        if (!this.currentSpaceship) return
        this.currentSpaceship.destination = new Geometry.Vector2(Entities.hexToWorld(point.x), Entities.hexToWorld(point.y))
        console.log(this.currentSpaceship.destination)
    }

    private shoot = () => {
        if (this.currentSpaceship && this.currentSpaceship.canShoot()) {
            const angle = Maths.getDegrees(Maths.calculateAngle(
                this.pointer.x,
                this.pointer.y,
                this.currentSpaceship.x,
                this.currentSpaceship.y,
            ))

            this.createAndSendAction('shoot', {
                angle: Math.round(angle),
            })

            this.currentSpaceship.shoot()
        }
    }

    private selfControlMoveIfNeeded = () => {
        this.currentSpaceship?.animate()

        if (this.currentSpaceship && this.currentSpaceship.destination) {
            const data = this.currentSpaceship.selfControl()
            if (!data) return

            const action = this.createAndSendAction('move', {
                x: data.dir.x,
                y: data.dir.y,
                vx: data.velocity.x,
                vy: data.velocity.y,
            })

            if (action) this.moveActions.push(action)
        }
    }

    private move = (dir: Geometry.Vector2) => {
        if (!this.currentSpaceship) return

        const velocity = this.currentSpaceship.move(dir)

        const action = this.createAndSendAction('move', {
            x: dir.x,
            y: dir.y,
            vx: velocity.x,
            vy: velocity.y,
        })

        if (action) this.moveActions.push(action)

        this.parallax.spaceshipMoved(
            velocity, 
            new Geometry.Vector2(this.currentSpaceship.x, this.currentSpaceship.y)
        )

        // Collisions: Map
        // const clampedPosition = this.map.clampCircle(this.currentPlayer.body);
        // this.currentPlayer.position = {
        //     x: clampedPosition.x,
        //     y: clampedPosition.y,
        // };
    };

    private updateMousePositionGlobally = () => {
        const mouse = this.app.renderer.plugins.interaction.mouse.global
        this.pointer.set(mouse.x + this.worldView.left, mouse.y + this.worldView.top)
    }

    private rotateShield = () => {
        if (!this.currentSpaceship) return

        this.updateMousePositionGlobally()

        const angle = Maths.getDegrees(Maths.calculateAngle(
            this.pointer.x,
            this.pointer.y,
            this.currentSpaceship.x,
            this.currentSpaceship.y,
        ))

        this.createAndSendAction('rotate', {
            angle: Maths.positiveAngle(angle),
        })
    };

    // PUBLIC METHODS

    leaveGameRoom() {
        if (!this.currentSpaceship) return
        this.currentSpaceship.resetSpaceship()
        this.spaceshipContainer.removeChild(this.currentSpaceship)
        this.currentSpaceship = undefined
    }

    // GETTERS && SETTERS

    setScreenSize = (screenWidth: number, screenHeight: number) => {
        this.worldView.resize(screenWidth, screenHeight, Constants.ZOOM_MAP_RADIUS * 2, Constants.ZOOM_MAP_RADIUS * 2)
    };

    // COLYSEUS: State

    setState = (state: GameState) => {
        this.state = state

        console.log(this.state.asteroids)

        state.onChange = (changes) => {
            changes.forEach(change => {
                if (change.field == 'planets') {
                    this.solarSystem.addPlanets(state.planets)
                }
            });
        };

        state.game.onChange = this.handleGameChange.bind(this)

        state.spaceships.onAdd = this.handleSpaceshipAdd.bind(this)
        state.spaceships.onRemove = this.handleSpaceshipRemove.bind(this)
        state.asteroids.onAdd = this.handleAsteroidAdd.bind(this)
        state.asteroids.onRemove = this.handleAsteroidRemove.bind(this)
        state.bullets.onAdd = this.handleBulletAdd.bind(this)
        state.bullets.onRemove = this.handleBulletRemove.bind(this)
        state.starbases.onAdd = this.handleStarbaseAdd.bind(this)
    }

    // COLYSEUS: Game

    handleGameChange = (attributes: any) => {
        for (const row of attributes) {
            this.gameUpdate(row.field, row.value)
        }
    }

    gameUpdate = (name: string, value: any) => {
        switch (name) {
            case 'roomName':
                this.roomName = value;
                break;
            default:
                break;
        }
    }

    // COLYSEUS: Starbase

    handleStarbaseAdd = (starbase: Starbase, key: number) => {
        console.log(`[Starbase][Add] ${starbase.nonce}`)
        const sprite = new StarbaseSprite(starbase)
        this.starbasesContainer.add(key.toString(), sprite)
    }

    // COLYSEUS: Spaceships

    handleSpaceshipAdd = (spaceship: Spaceship, spaceshipId: string) => {
        console.log(`[Spaceship][Add] ${spaceship.nonce}`)
        spaceship.onChange = () => {
            this.handleSpaceshipUpdate(spaceship, spaceshipId)
        }

        this.createSpaceshipSprite(spaceship, spaceshipId)
    }

    handleSpaceshipUpdate = (attributes: Spaceship, spaceshipId: string) => {
        const isMe = playerManager.isPlayerIdMe(attributes.playerId)
        console.log(`[Spaceship][Update] nonce ${attributes.nonce} is me: ${isMe}`)
        if (isMe) {
            const ghost = this.spaceshipContainer.get(spaceshipId)
            if (!ghost) return

            // Player loaded after the spaceship
            if (!this.currentSpaceship) {
                const player = this.state?.players[attributes.playerId]
                ghost.updatePlayer(isMe, player)
                this.loadAndCreateSpaceshipSprite(attributes, player)
                return
            }

            if (attributes.ack !== this.currentSpaceship.ack) {
                this.currentSpaceship.ack = attributes.ack

                // Update ghost position
                ghost.update({
                    x: attributes.position.x,
                    y: attributes.position.y,
                    angle: attributes.position.angle
                })

                if (isMe) {
                    ghost.toX = attributes.position.x
                    ghost.toY = attributes.position.y
                    ghost.toAngle = attributes.position.angle
                }

                // Run simulation of all movements that weren't treated by server yet
                const index = this.moveActions.findIndex((action) => action.ts === attributes.ack)
                this.moveActions = this.moveActions.slice(index + 1)
                this.moveActions.forEach((action) => {
                    const preVelocity = new Geometry.Vector2(action.value.vx, action.value.vy)
                    ghost.move(action.value, preVelocity, false)
                })

                // Check if our predictions were accurate
                const distance = Maths.getDistance(this.currentSpaceship.x, this.currentSpaceship.y, ghost.x, ghost.y)
                if (distance > 0) {
                    console.warn(`Corrected position distance=${distance}`)
                    this.currentSpaceship.update({
                        x: ghost.x,
                        y: ghost.y,
                        angle: ghost.angle
                    })
                }
            } else {
                this.updateSpaceshipServerPosition(attributes, spaceshipId)
            }
        } else {
            this.updateSpaceshipServerPosition(attributes, spaceshipId)
        }
    }

    handleSpaceshipRemove = (spaceship: Spaceship, spaceshipId: string) => {
        const isMe = playerManager.isPlayerIdMe(spaceship.playerId)
        this.spaceshipContainer.remove(spaceshipId);

        // If the player is "you"
        if (isMe && this.currentSpaceship) {
            this.spaceshipContainer.removeChild(this.currentSpaceship);

            delete this.currentSpaceship;
        }
    }

    async loadAndCreateSpaceshipSprite(spaceship: Spaceship, player: Player) {
        const texture = await SpaceshipSprite.loadSpaceshipTexture(spaceship)
        this.createSimSpaceshipSprite(spaceship, player, texture)
    }

    createSimSpaceshipSprite(spaceship: Spaceship, player: Player, texture: PIXI.Texture) {
        if (this.currentSpaceship) return console.log('Already exists')
        this.currentSpaceship = new SpaceshipSprite(spaceship, texture, false, player)
        this.currentSpaceship.zIndex = ZINDEXES.ME
        this.spaceshipContainer.addChild(this.currentSpaceship)
        this.worldView.follow(this.currentSpaceship)

        this.parallax.initialStars(spaceship.position)
        this.changeHudSpaceship?.(this.currentSpaceship)
        console.log(`Following began for ${spaceship.nonce}`)
    }

    async createSpaceshipSprite(spaceship: Spaceship, spaceshipId: string) {

        const texture = await SpaceshipSprite.loadSpaceshipTexture(spaceship)
        const isMe = playerManager.isPlayerIdMe(spaceship.playerId)
        const player = this.state?.players[spaceship.playerId]

        const sprite = new SpaceshipSprite(spaceship, texture, isMe, player)
        this.spaceshipContainer.add(spaceshipId, sprite)

        // If the player is "you"
        console.log(`Is me ${isMe} - current: ${playerManager.playerId} - spaceship ${spaceship.nonce}: ${spaceship.playerId}`)
        if (isMe) {
            this.createSimSpaceshipSprite(spaceship, player, texture)
        }
    }

    updateSpaceshipServerPosition(attributes: Spaceship, spaceshipId: string) {
        const spaceship = this.spaceshipContainer.get(spaceshipId)
        if (!spaceship) return

        // Update position
        spaceship.update({
            x: spaceship.toX,
            y: spaceship.toY,
            angle: spaceship.toAngle
        });
        spaceship.toX = attributes.position.x
        spaceship.toY = attributes.position.y
        spaceship.toAngle = attributes.position.angle
    }

    // COLYSEUS: Asteroids

    handleAsteroidAdd = (asteroid: Asteroid, key: string) => {
        console.log('[Asteroid][Add]')
        const sprite = new AsteroidSprite(asteroid, this.eventHandler)
        this.asteroidContainer.add(key, sprite)
    }

    handleAsteroidRemove = (asteroid: Asteroid, key: string) => {
        console.log('[Asteroid][Remove]')
        this.asteroidContainer.remove(key);
    }

    // COLYSEUS: Bullets

    handleBulletAdd = (bullet: Bullet, key: number) => {
        console.log('[Bullet][Add]')
        const sprite = new BulletSprite(bullet)
        this.bulletContainer.add(key.toString(), sprite)
    }

    handleBulletRemove = (bullet: Bullet, key: number) => {
        console.log('[Bullet][Remove]')
        this.bulletContainer.remove(key.toString());
    }
