import React from 'react';
import { EventHandler } from '@krogan/common';
import { useRoomContext } from 'context/room';
import { Box, Button, Inline, Space, Text, View } from 'gui/uielements';
import { Spaceship } from 'state/Spaceship';
import { useCoreContext } from 'core';

/**
 * A menu displaying tile contents
 */
export function SpaceshipDetails(props: { 
	spaceship: Spaceship,
	eventHandler: EventHandler
}): React.ReactElement {

    const { hud } = useRoomContext()
    const { address } = useCoreContext()
    const { spaceship, eventHandler } = props;

    return (
        <View fullscreen flex center backdrop popup>
            <Box popup>
                {/* Share */}
                <Text center type='title'>Krogan #{spaceship.nonce}</Text>
                <Space size="xs" />

                {spaceship.address == address && (
                    <>
                    <Button onClick={() => eventHandler.send('spaceshipControl', spaceship, hud.controlledSpaceship)}>
                        TAKE CONTROL
                    </Button>

                    <Space size="s" />

                    <View flex center>
                        <Button onClick={() => eventHandler.send('warpInitiate', spaceship)}>
                            WARP TO
                        </Button>
                        <Inline size="s" />
                        <Button onClick={() => {alert('Coming soon!')}}>
                            PATROL
                        </Button>
                    </View>

                    <Space size="xs" />
                    </>
                )}

                <View flex>
                    <Button reversed onClick={() => eventHandler.send('closePopup', 'close')}>
                        Close
                    </Button>
                </View>
            </Box>
        </View>
    );
}
