import { ShipTexture } from 'images/textures';
import { Container, MIPMAP_MODES, Sprite, Texture } from 'pixi.js';
import { PathPosition } from './MapPathContainer';

export default class SpaceshipIcon extends Container {

	private _sprite: Sprite

    // Init
    constructor(x: number, y: number) {		
		super()

		const size = 64
		this.x = x
		this.y = y
		this.zIndex = 100

		const texture = Texture.from(ShipTexture)
		texture.baseTexture.mipmap = MIPMAP_MODES.POW2;

        this._sprite = new Sprite(texture)
		this._sprite.anchor.x = 0.5
		this._sprite.anchor.y = 0.5
		this._sprite.width = size
		this._sprite.height = size
		this.addChild(this._sprite)
    }

	alignOnPath(position: PathPosition) {
		this.x = position.position.x
		this.y = position.position.y
		this.angle = position.angle
	}

}
