import { getCacheSheet } from 'images/textures';
import { AnimatedSprite, Spritesheet } from 'pixi.js';
import { Bullet } from 'state/Bullet';

/**
 * A sprite representing a bullet with circle bounds.
 */
export default class BulletSprite extends AnimatedSprite {

    constructor(
        bullet: Bullet
    ) {
		const sheet = getCacheSheet('bullet-green') as Spritesheet
		super(sheet.animations["Missile-B"])

        this.width = 5
        this.height = 76
		this.angle = bullet.position.angle + 90
		this.position.set(bullet.position.x, bullet.position.y)

		this.animationSpeed = 0.3
		this.updateAnchor = true
		this.loop = false
		this.play()

		bullet.position.onChange = () => {
			console.log('[Bullet][Position][Update]')
			this.position.set(bullet.position.x, bullet.position.y)
		}
    }
}
