import { Constants } from '@krogan/common';
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { Button, Space, Box, View, Text } from 'gui/uielements';
import { useRoomContext } from 'context/room';
import { Username } from 'gui/signup';
import { SelectNfts } from 'gui/signup/SelectNfts';
import { ConnectButton, useCoreContext } from 'core';
import { useLobbyManager } from 'rooms';
import { Image } from 'gui/uielements/Image';


const Home = (): React.ReactElement => {

    const navigate = useNavigate()
    const lobbyManager = useLobbyManager()
    const { isLoggedIn, address } = useCoreContext()
    const { player } = useRoomContext()

    // LIFECYCLE
    useEffect(() => {
        init()
        return function cleanup() {
            deinit()
        }
    }, [])

    const init = async () => {}

    const deinit = () => {
        lobbyManager.leave();
    }

    // EFFECTS
    const connection = () => {
        if (isLoggedIn) { 
            lobbyManager.connectToLobby(address)
        } else {
            lobbyManager.leave()
        }
    }
    useEffect(connection, [isLoggedIn])

    const start = () => {
        lobbyManager.leave()
        navigate('/game')
    }

    // RENDER

    return (
        <>
        <View
            flex
            center
            style={{
                padding: 32,
                flexDirection: 'column',
            }}
        >
            <Helmet>
                <title>{`${Constants.APP_TITLE} - Home`}</title>
            </Helmet>

            <View flex center column>
                <Image src="/images/branding.png" />
            </View>

            <Space size="m"/>

            <Box style={{ width: 500, maxWidth: '100%', textAlign: 'center' }}>

                {/* <ConnectButton /> */}

                {lobbyManager.message && 
                <>
                    <Space size="s" />
                    <Text type="body">{lobbyManager.message}</Text>
                </>
                }

                {isLoggedIn && player && 
                <>
                    <p>{JSON.stringify(player)}</p>
                    <p>{player?.nfts?.join(', ')}</p>
                    {player.username == undefined &&
                        <Username onSave={lobbyManager.updateUsername} />
                    }

                    {player.username && player.nfts.length == 0 && 
                        <SelectNfts player={player} selected={lobbyManager.updateSpaceships} />
                    }

                    <Space size="s" />

                    {player.username && player.nfts.length > 0 &&
                        <Button text="Start game" onClick={start} />
                    }
                </>
                }
            </Box>
        </View>
        </>
    );
}

export default Home;
