import { EventHandler } from '@krogan/common';
import { useRoomContext } from 'context/room';
import { Box, Button, Inline, Space, Text, View } from 'gui/uielements';
import { IPoint } from 'pixi.js';
import React, { CSSProperties } from 'react';

/**
 * A menu displaying tile contents
 */
export function PointDetails(props: { 
	point: IPoint,
	eventHandler: EventHandler
}): React.ReactElement {

    const { point, eventHandler } = props;
	const { gameRoom } = useRoomContext()

    return (
        <View fullscreen flex center backdrop popup>
            <Box popup>
                {/* Share */}
                <Text center type='title'>{JSON.stringify(point)}</Text>

				{gameRoom &&
					<Button onClick={() => eventHandler.send('spaceshipSelfControl', {
						type: 'goto',
						point: point
					})}>
						GO TO
					</Button>
				}

                <Space size="xs" />

				<Button reversed onClick={() => eventHandler.send('closePopup', 'close')}>
					Close
				</Button>               
            </Box>
        </View>
    );
}
