import { Inline, Text } from 'gui/uielements';
import React, { CSSProperties } from 'react';
import { Container } from '.';
import { IconButton } from '../uielements/IconButton';
import { Menu } from '../../images/icons';
import { isMobile } from 'react-device-detect';

/**
 * Render the players count.
 */
export const Players = React.memo(
    (props: {
        count?: number;
        style?: CSSProperties;
        onMenuClicked: () => void;
    }): React.ReactElement => {
        const { count, style, onMenuClicked: onMenuPressed } = props;
        const playersText = `Players (${count})`;

        return (
            <Container
                style={{
                    ...styles.players,
                    ...style,
                }}
            >
                <Text type='body' style={styles.playersText}>{playersText}</Text>
                <Inline size="xs" />
                <IconButton
                    icon={Menu}
                    style={{
                        ...styles.menuButton,
                        ...(isMobile ? { width: 40, height: 40 } : {}),
                    }}
                    onClick={onMenuPressed}
                />
            </Container>
        );
    },
);

const styles: { [key: string]: CSSProperties } = {
    players: {
        flexDirection: 'row',
    },
    playersText: {
        color: 'white',
        fontSize: isMobile ? 14 : 16,
    },
    menuButton: {},
};
