import { clientUrl } from 'utils/server';
import * as PIXI from 'pixi.js'
import MapTexture from './map.png';
import NoGoZoneTexture from './nogozone.png';
import HiddenTexture from './hidden.png';
import TargetTexture from './target.png';
import ShieldTexture from './shield.png';
import ShipTexture from '../uikit/ship-icon.png';
import StarbaseTexture from '../uikit/starbase-icon.png';
import UserTexture from '../uikit/user-icon.png'
import UsersTexture from '../uikit/users-icon.png'

export {
	MapTexture,
	NoGoZoneTexture,
	HiddenTexture,
	ShipTexture,
	TargetTexture,
	UserTexture,
	UsersTexture,
	StarbaseTexture,
};

export enum Asset {
	MapBackground = 'mapBackground',
	NoGoZone = 'noGoZone',
}

export const loadMapAssets = (complete: () => void) => {

	const keys: string[] = []

	for (let i = 1; i <= 5; i++) {
		let url = clientUrl() + `/sprites/planets/mini/${i}.png`
		PIXI.Assets.add(`planet-mini-${i}`, url)
		url = clientUrl() + `/sprites/planets/medium/${i}.png`
		PIXI.Assets.add(`planet-medium-${i}`, url)

		keys.push(`planet-mini-${i}`)
		keys.push(`planet-medium-${i}`)
	}

	for (let i = 1; i <= 5; i++) {
		let url = clientUrl() + `/sprites/asteroids/empty/${i}-1.png`
		PIXI.Assets.add(`asteroid-empty-${i}-1`, url)
		url = clientUrl() + `/sprites/asteroids/empty/${i}-2.png`
		PIXI.Assets.add(`asteroid-empty-${i}-2`, url)

		keys.push(`asteroid-empty-${i}-1`)
		keys.push(`asteroid-empty-${i}-2`)
	}

	PIXI.Assets.add('sun-mini-1', clientUrl() + `/sprites/planets/mini/sun1.png`)
	keys.push('sun-mini-1')
	PIXI.Assets.add('sun-medium-1', clientUrl() + `/sprites/planets/medium/sun1.png`)
	keys.push('sun-medium-1')

	// Laser
	PIXI.Assets.add('laser-beam', clientUrl() + `/sprites/actions/laser.png`)
	keys.push('laser-beam')
	PIXI.Assets.add('beam-sparks', clientUrl() + '/sprites/actions/beam-sparks-sheet.json')
	keys.push('beam-sparks')

	// Thrusters
	PIXI.Assets.add('thruster-1-s', clientUrl() + '/sprites/actions/thrusters-fire-start.json')
	keys.push('thruster-1-s')
	PIXI.Assets.add('thruster-1-m', clientUrl() + '/sprites/actions/thrusters-fire-move.json')
	keys.push('thruster-1-m')
	PIXI.Assets.add('thruster-2-s', clientUrl() + '/sprites/actions/thrusters-main-start.json')
	keys.push('thruster-2-s')
	PIXI.Assets.add('thruster-2-m', clientUrl() + '/sprites/actions/thrusters-main-move.json')
	keys.push('thruster-2-m')
	PIXI.Assets.add('thruster-3-s', clientUrl() + '/sprites/actions/thrusters-special-start.json')
	keys.push('thruster-3-s')
	PIXI.Assets.add('thruster-3-m', clientUrl() + '/sprites/actions/thrusters-special-move.json')
	keys.push('thruster-3-m')

	// Map items
	PIXI.Assets.add('stars', clientUrl() + '/sprites/environment/stars.json')
	keys.push('stars')

	// Bullets
	PIXI.Assets.add('bullet-green', clientUrl() + '/sprites/bullets/green.json')
	keys.push('bullet-green')

	PIXI.Assets.add('mapBackground', MapTexture)
	keys.push('mapBackground')
	PIXI.Assets.add('noGoZone', NoGoZoneTexture)
	keys.push('noGoZone')
	PIXI.Assets.add('shield', ShieldTexture)
	keys.push('shield')

	PIXI.Assets.load(keys, (progress) => {
		if (progress == 1.0) {
			complete()
		}
	})
}

export const getCacheAsset = (asset: Asset | string): PIXI.Texture<PIXI.Resource> | undefined => {
	return PIXI.Assets.get(asset).texture
}

export const getCacheSheet = (asset: Asset | string): PIXI.Spritesheet | undefined => {
	return PIXI.Assets.get(asset).spritesheet;
}

export const getSpaceshipImage = (level: number, image: string) => {
	return `https://krogancoin.com/QmeeQK6V9uv71xyNbGy5q1PHRuBHy1EYXVMs9sS5bP1iYY/${level}/${image}`
}

export const getQwtSpaceshipImage = () => {
	return clientUrl() + '/sprites/QoWatt.png'
}

export const getSpriteImage = (path: string) => {
	return clientUrl() + '/sprites' + path
}
