export const APP_TITLE = 'KROGANVERSE';
// General
export const FPS = 50;
export const WS_PORT = 1375;
export const PLAYERS_REFRESH = 1000;
export const DEBUG = false;
// Rooms
export const ROOM_LOBBY_NAME = 'lobby';
// Game
export const GAME_SPEED = 1;
export const PLAYER_NAME_MAX = 16;
export const LOG_LINES_MAX = 5;
// COLORS
export const BACKGROUND_COLOR = '#08070C';
export var TileColor;
(function (TileColor) {
    TileColor[TileColor["DEFAULT"] = 5875897] = "DEFAULT";
    TileColor[TileColor["HIGHLIGHT"] = 9627899] = "HIGHLIGHT";
    TileColor[TileColor["HIDDEN"] = 2631720] = "HIDDEN";
})(TileColor || (TileColor = {}));
export var TeamColor;
(function (TeamColor) {
    TeamColor[TeamColor["RED"] = 13716299] = "RED";
    TeamColor[TeamColor["BLUE"] = 4961489] = "BLUE";
    TeamColor[TeamColor["GREEN"] = 8309118] = "GREEN";
})(TeamColor || (TeamColor = {}));
// Tile (Hex)
export const TILE_SIZE = 32;
const TIME_PER_TILE = 10;
export const PATH_REFRESH = TIME_PER_TILE / 5 * 1000;
export const PATH_TIME = (length) => TIME_PER_TILE * (length - 1) * 1000;
// Player (circle)
export const SPACESHIP_ROTATION = 90;
export const PLAYER_SIZE = 32;
export const SPACE_FRICTION = 0.06;
export const ROTATION_SPEED = 30;
// ---- Messages ----
// Client
export const SET_USERNAME = 'set_username';
export const SET_SPACESHIPS = 'set_spaceships';
// -------------------
// Collections
export const KROGAN_MAIN = 'KROGAN-54c361';
// --- Map ---
export const ZOOM_MAP_RADIUS = 12000;
export const HEX_RADIUS = 100;
export const MAP_SIZE = 8 * HEX_RADIUS * 2;
export const TEAMS = [{
        id: 'team_1',
        color: 0xD14B4B,
        name: 'Team RED'
    }, {
        id: 'team_2',
        color: 0x4BB4D1,
        name: 'Team BLUE'
    }, {
        id: 'team_3',
        color: 0x7EC97E,
        name: 'Team GREEN'
    }];
