import React, { CSSProperties } from 'react';
import { EventHandler } from '@krogan/common';
import { Box, Button, Inline, Space, Text, View } from 'gui/uielements';
import { TileContainer } from 'sprites';
import { Image } from 'gui/uielements/Image';
import { getSpaceshipImage } from 'images/textures';
import { useCoreContext } from 'core';

/**
 * A menu displaying tile contents
 */
export function TileDetails(props: { 
	tile: TileContainer,
	eventHandler: EventHandler
}): React.ReactElement {

    const { address } = useCoreContext()
    const { tile, eventHandler } = props

    const spaceships = () => {
        if (!tile.hexagon) return <></>
        const spaceships = Array.from(tile.hexagon.spaceships.values())
        return spaceships.map((spaceship, i) => (
            <View key={i}>
                <View flex>
                    <View center flexEqual textCenter>
                        <Image src={getSpaceshipImage(spaceship.info.level, spaceship.info.image)} style={styles.image} />
                        <Space size="xxs" />
                        <Text type='body'>Krogan #{spaceship.nonce}</Text>
                    </View>
                    <View center flexEqual>
                        <Text type='body'>Spaceship stats</Text>
                        <Space size="xxs" />
                        <Text type='body'>Placeholder</Text>
                    </View>
                </View>
                <Space size="xxs" />
                {spaceship.address == address && (
                    <>
                    <View flex center>
                        <Button onClick={() => eventHandler.send('warpInitiate', spaceship)}>
                            WARP TO
                        </Button>
                        <Inline size="s" />
                        <Button onClick={() => {alert('Coming soon!')}}>
                            PATROL
                        </Button>
                    </View>
                    <Space size="s" />
                    </>
                )}
            </View>            
        ))
    }

    return (
        <View fullscreen flex center backdrop popup>
            <Box popup>
                {/* Share */}
                <Text center type='title'>{tile.coordinate.toString()}</Text>
                <Space size="xs" />

				{tile.hexagon && tile.hexagon.spaceships.size > 0 && 
				<>
					<Text type='alt'>Your spaceships at this location</Text>
					<Space size="s" />
				</>
				}

				{spaceships()}

                <Space size="m" />

                <Button onClick={() => eventHandler.send('hexExplore', tile)}>
                    EXPLORE
                </Button>

                <Space size="xs" />

                <View flex>
                    <Button reversed onClick={() => eventHandler.send('closePopup', 'close')}>
                        Close
                    </Button>
                </View>
            </Box>
        </View>
    );
}

const styles: { [key: string]: CSSProperties } = {
    image: {
        width: '100px'
    },
};
