//// --------------------- ///
Array.prototype.isEmpty = function () {
    return this.length == 0;
};
Array.prototype.insert = function (index, item) {
    this.splice(index, 0, item);
};
Array.prototype.serialized = function (min = false) {
    if (min)
        return this.map((obj) => obj.serializeMin());
    return this.map((obj) => obj.serialize());
};
Array.prototype.remove = function (element) {
    const index = this.indexOf(element);
    if (index > -1) {
        this.splice(index, 1);
        return true;
    }
    return false;
};
Array.prototype.last = function () {
    if (this.length > 0) {
        return this[this.length - 1];
    }
    return null;
};
Array.prototype.first = function () {
    if (this.length > 0) {
        return this[0];
    }
    return null;
};
Date.prototype.addSeconds = function (seconds) {
    const myTimeSpan = seconds * 1000;
    this.setTime(this.getTime() + myTimeSpan);
    return this;
};
/**
 * String functions
 */
String.prototype.truncate = function (strLen, separator) {
    if (this.length <= strLen)
        return "" + this;
    separator = separator || '...';
    const sepLen = separator.length, charsToShow = strLen - sepLen, frontChars = Math.ceil(charsToShow / 2), backChars = Math.floor(charsToShow / 2);
    return this.substr(0, frontChars) +
        separator +
        this.substr(this.length - backChars);
};
export {};
