import { Box, View } from 'gui/uielements';
import { Image } from 'gui/uielements/Image';
import { getSpaceshipImage } from 'images/textures';
import React, { CSSProperties, Fragment } from 'react';
import { Spaceship } from 'state/Spaceship';
import { Container } from '.';

/**
 * Render the details about the current spaceship in control
 */

export interface SpaceshipPanelsProps {
	style?: CSSProperties
	spaceship: Spaceship // map synced spaceship
}

export interface SpaceshipPanelsState {}

class SpaceshipPanelsView extends React.Component<SpaceshipPanelsProps, SpaceshipPanelsState> {

	constructor(props: SpaceshipPanelsProps) {
		super(props)
	}

	componentDidMount(): void {
		this.props.spaceship.stats.listen('damageMap', () => {
			this.forceUpdate()
		})
	}

	styleForDamage = (damage?: number): CSSProperties | undefined => {
		if (!damage) return undefined
		if (damage <= 0) {
			return styles.damageTotal
		}
		if (damage < 30) {
			return styles.damageHard
		}
		if (damage < 60) {
			return styles.damageMedium
		}
		if (damage < 90) {
			return styles.damageLight
		}
		return styles.damageNone
	}

	render() {
		return (
		<Container
			className="box-empty"
			style={{
				...this.props.style,
			}}
		>
			<Fragment>
				<View flex style={styles.damageMap}>
					<View flex column style={styles.damageMapColumn}>
						<Box style={styles.emptyItem}><p></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('0-3')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('0-2')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('0-1')),
						}}><p style={styles.text}></p></Box>
						<Box style={styles.emptyItem}><p style={styles.text}></p></Box>
					</View>
					<View flex column style={styles.damageMapColumn}>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('1-4')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('1-3')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('1-2')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('1-1')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('1-0')),
						}}><p style={styles.text}></p></Box>
					</View>
					<View flex column style={styles.damageMapColumn}>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('2-4')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('2-3')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('2-2')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('2-1')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('2-0')),
						}}><p style={styles.text}></p></Box>
					</View>
					<View flex column style={styles.damageMapColumn}>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('3-4')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('3-3')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('3-2')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('3-1')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('3-0')),
						}}><p style={styles.text}></p></Box>
					</View>
					<View flex column style={styles.damageMapColumn}>
						<Box style={styles.emptyItem}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('4-3')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('4-2')),
						}}><p style={styles.text}></p></Box>
						<Box style={{
							...styles.damageMapItem,
							...this.styleForDamage(this.props.spaceship.stats.damageMap.get('4-1')),
						}}><p style={styles.text}></p></Box>
						<Box style={styles.emptyItem}><p></p></Box>
					</View>
					<Image 
						src={getSpaceshipImage(this.props.spaceship.info.level, this.props.spaceship.info.image)} 
						style={styles.spaceship}
					/>
				</View>
			</Fragment>
		</Container>
		)
	}

}

