import { Room } from "colyseus.js";
import { Session } from "core";
import { MapState } from "state/MapState";
import { Player } from "state/Player";
import { Models } from "@krogan/common";
import { MapSpaceshipSprite, SpaceshipSprite, TileContainer } from "sprites";
import { GameState } from "state/GameState";
import { IPoint } from "pixi.js";
import { Asteroid } from "state/Asteroid";

export interface HudType {
    backButtonVisible: boolean,
    playerId: string,
    messages: Models.MessageJSON[],
    announce: string,
    stats?: Models.StatsJSON,
	selectedPoint?: IPoint,
    selectedTile?: TileContainer,
    selectedSpaceship?: MapSpaceshipSprite,
    controlledSpaceship?: SpaceshipSprite,
    selectedAsteroid?: Asteroid,
}

export interface StateType {
	gameRoom?: Room<GameState>;
	mapRoom?: Room<MapState>;
	lobbyRoom?: Room<MapState>;
	player?: Player,
	hud: HudType,
	spaceships: Models.ISpaceship[]
}

export const initialState = (): StateType => {
	return {
		player: Session.getItem('player'),
		hud: {
			backButtonVisible: false,
			playerId: '',
			messages: [],
			announce: '',
		},
		spaceships: []
	}
}
