import { Player } from '../state/Player';

class PlayerManager {

	private _playerId: string = ''
	private _me?: Player

    private subscribers: (() => void)[] = []

    // PUBLIC METHODS

	isPlayerIdMe = (id: string) => {
        return this._playerId === id
    }

    username = () => {
        return this._me ? this._me.username : ''
    }

    address = () => {
        return this._me ? this._me.address : ''
    }

    // GETTERS & SETTERS

    get playerId() {
        return this._playerId
    }

    set playerId(id: string) {
        this._playerId = id
        this.subscribers.forEach(subs => subs())
    }

    set me(player: Player | undefined) {
        this._me = player
        this.subscribers.forEach(subs => subs())
    }

    onUpdate(callback: () => void) {
        this.subscribers.push(callback)
    }
}

export const playerManager = new PlayerManager()
