import { AnchorContainer } from "sprites/util/AnchorContainer"
import IconText, { Stat } from "./IconText"

export default class StatsStack extends AnchorContainer {

	private _stats: IconText[]

    constructor(stats: Stat[]) {
		super()

		this._stats = stats.map((stat, i) => {
            const statSprite = new IconText(stat)
			statSprite.y = statSprite.height /2 + i * statSprite.height * 0.7
			this.addChild(statSprite)
			return statSprite
        })
		this.anchorX = 0.5
		this.anchorY = 0
    }

}
