import { Text, TextStyle } from 'pixi.js';

export default class TextSprite extends Text {
    constructor(text: string, fontSize: number, anchorX: number, anchorY: number, style?: Record<string, unknown>) {
        super(
            '',
            new TextStyle({
                fontSize,
                fill: '#ffffff',
                fontFamily: 'Arcadepix',
                align: 'left',
                fontWeight: 'bold',
                stroke: 'black',
                strokeThickness: 2,
                ...style,
            }),
        );
        this.anchor.set(anchorX, anchorY);
        this.text = text;
    }

    // Getters
    get left() {
        return this.x;
    }

    get right() {
        return this.x + this.width;
    }

    get top() {
        return this.y;
    }

    get bottom() {
        return this.y + this.height;
    }
}
