export default class EventHandler {
    constructor() {
        this.listeners = {};
        // blank
    }
    on(event, listener) {
        if (this.listeners[event] && !this.listeners[event].includes(listener)) {
            this.listeners[event].push(listener);
        }
        else {
            this.listeners[event] = [listener];
        }
    }
    /**
     * Send a global event.
     * @param event Event Type
     * @param args Send all the args needed for the callback to process. Context will be old inside the callback.
     */
    send(event, ...args) {
        console.log(`New event ${event}`);
        console.log(args);
        console.log(`------------------`);
        const listeners = this.listeners[event];
        if (listeners && listeners.length > 0) {
            listeners.forEach(listener => {
                listener(...args);
            });
        }
    }
    remove(event, listener) {
        if (this.listeners[event] && this.listeners[event].includes(listener)) {
            this.listeners[event].remove(listener);
        }
    }
}
